    syncRefData: function(referenceObject, instanceGR, cmdbClass, refTables, nestedLevel) {

        var localPayload = {};
        var currentLevelFields = [];
        var previousLevelFields = [];

        for (var i = 0; i < referenceObject.length; i++) {
