                        var putResponse = this.executeRESTCall(putUrl, 'PUT', refRecord, refTable);
                        localPayload[field] = returnObj.responseBody.result[0].sys_id;
                        this.setlogging('info', "New PUT sys_id is: " + returnObj.responseBody.result[0].sys_id);
                    }
                    if (currentLevelFields.length > 0) {
                        this.setlogging('info', "currentLevelFields: " + JSON.stringify(currentLevelFields, "", 2));
