                        currentLevelFields = [];
                    }
                    this.setlogging('info', "Deeper to nestedlevel " + (nestedLevel + 1) + " from field: " + key);
                    this.syncRefData(referenceObject[i][key], instanceGR, cmdbClass, refTables, nestedLevel + 1);
                } else {
                    currentLevelFields.push(referenceObject[i]);
                }
            }
        }
