sysevent_script_action.script:
gs.info("Start Script action");
var eventParm1 = JSON.parse(event.parm1);
var scriptInclude = new NestedLVLSync();
//var firstRow = eventParm1.firstRow;
//var lastRow = eventParm1.lastRow;
var sourTable = eventParm1.sourTable;
var nestedLevel = eventParm1.nestedLevel;
var sysIds = JSON.parse(eventParm1.sysIds);
var refTables = JSON.parse(event.parm2);
gs.info("Vlad sourTable is " + sourTable);
gs.info("Vlad refTables are " + JSON.stringify(refTables));
for (var i = 0; i < sysIds.length; i++) {
	gs.info("Vlad Index of i is " + i);
    var sysId = sysIds[i];
    var searchGR = new GlideRecord(sourTable);
    if (searchGR.get(sysId)) {
		gs.info("Vlad SearchGR is " + JSON.stringify(searchGR));
		gs.info("Vlad Sys ID is " + sysId);
        scriptInclude.syncData(searchGR, sourTable, refTables,nestedLevel);
    }
}
